async function fetchTopTracks() {
    const token = 'Bearer BQD4lY_FhvIvQD_GiJX3uPKvbmLG2-YIDykbmgagQo8By8t-n6CFh2KERwTsUB3FyJPgQ7S77EcsjLSXkp_sMtaTLmpBNpZyZ98bxgYjyCG1EskN5eE';
    try {
      const response = await fetch('https://api.spotify.com/v1/me/top/tracks?time_range=long_term&limit=5', {
        headers: {
          'Authorization': token
        }
      });
      const data = await response.json();
      return data;

    } catch (error) {
      console.error('Error fetching top tracks:', error);
      return [];
    }
  }


  export default function Spotify() {
    const [tracks, setTracks] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
      async function fetchData() {
        try {

          const topTracks = await fetchTopTracks();

          console.log(topTracks);

          setTracks(topTracks);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching data:', error);
          setLoading(false);
        }
      }

      fetchData();
    }, []);

    return (
      <div>
        <h2>Top 5 Tracks</h2>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <ul>
            {tracks.map((track, index) => (
                <div key={index} className="track">
                <p>{track.name}</p>
                </div>
            ))}
          </ul>
        )}
      </div>
    );
  }
