import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useNavigate } from "react-router-dom";
import LoginForm from "./LoginForm";
import Dashboard from "./Dashboard";
import Profile from "./Profile";
import Travel from "./component/Travel";


function importAll(r) {
  return r.keys().map(r);
}

// Import all images from the './images' directory
const images = importAll(require.context('./images/', false, /\.(png|jpe?g|JPG|svg)$/));


export default function App() {


  const [auth, setAuth] = useState({
    token: "",
    user: {},
    meta: {}
  });

  const onLoginSuccess = auth => {
    setAuth(auth);
  };

  const onLogOut = e => {
    setAuth({
      token: "",
      user: {},
      meta: {}
    });
  };

  const currentPage = () => {
    return auth.token ? (
      <Dashboard auth={auth} />
    ) : (
      <LoginForm onLoginSuccess={onLoginSuccess} />
    );
  };

  return (
    <Router>
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-info">
        <Link className="navbar-brand mr-auto" href="/">AppCo</Link>
        {auth.token && (
          <ul className="navbar-nav">
            <li className="nav-item active">
              <Link className="nav-link" to="/">Home <span className="sr-only">(current)</span></Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/profile">PFP</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/travel">Travel</Link>
              </li>
            <li className="nav-item">
            <a className="nav-link" style={{ textDecoration: 'underline' }} onClick={onLogOut}>Log out</a>
            </li>
          </ul>
        )}
      </nav>
      <div className="container">
      <Routes>
            {auth.token ? (
              <>
                <Route path="/" element={<Dashboard auth={auth} />} />
                <Route path="/profile" element={<Profile />} />
                <Route path="/travel" element={<Travel images={images} />} />
                <Route path="*" element={<Navigate to="/" />} />
              </>
            ) : (
              <Route path="/" element={<LoginForm onLoginSuccess={onLoginSuccess} />} />
            )}
          </Routes>
      </div>
    </div>
    </Router>
  );
}
