import React, { useState } from 'react';

const Travel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      <img 
        src={images[currentIndex]} 
        alt={`Image ${currentIndex}`} 
        style={{ width: '100%', height: '100%', objectFit: 'cover'}} 
      />
      <div style={{ position: 'absolute', bottom: -30, left: '10px' }}>
        <button onClick={prevImage} style={{ fontSize: 'inherit', padding: '6px 20px'}}>前</button>
      </div>

      <div style={{position: 'absolute', bottom: -30, right: '10px' }}>
        <button onClick={nextImage} style={{ fontSize: 'inherit', padding: '6px 20px'}}>次</button>
      </div>
    </div>
  );
};

export default Travel;
