import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useNagivate } from "react-router-dom";
import LoginForm from "./LoginForm";
import Dashboard from "./Dashboard";
import Profile from "./Profile";

export default function App() {
  const nagivate = useNagivate();

  const [auth, setAuth] = useState({
    token: "",
    user: {},
    meta: {}
  });

  const onLoginSuccess = auth => {
    setAuth(auth);
    navigate('/dashboard');
  };

  const onLogOut = e => {
    setAuth({
      token: "",
      user: {},
      meta: {}
    });
  };

  const currentPage = () => {
    return auth.token ? (
      <Dashboard auth={auth} />
    ) : (
      <LoginForm onLoginSuccess={onLoginSuccess} />
    );
  };

  return (
    <Router>
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-info">
        <a className="navbar-brand mr-auto" href="#">AppCo</a>
        {auth.token && (
          <ul className="navbar-nav">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            {/* Link to the Tasks page */}
            <li className="nav-item">
                <Link className="nav-link" to="/profile">PFP</Link>
              </li>
            <li className="nav-item">
              <a className="nav-link" onClick={onLogOut}>Log out</a>
            </li>
          </ul>
        )}
      </nav>
      <div className="container">
        <currentPage />
          <Routes>
            <Route path="/" element={<LoginForm onLoginSuccess={onLoginSuccess} />} />
            <Route path="/dashboard" element={<Dashboard auth={auth} />} />
            <Route path="/profile" element={<Profile />} />
          </Routes>
      </div>
    </div>
    </Router>
  );
}
