import React from "react";
import { Link, useLocation, Navigate } from "react-router-dom";

const MangaLink = () => {
    const location = useLocation();

    // Check if the current location is already the manga page
    const isMangaPage = location.pathname === '/manga';

    // If already on the manga page, navigate to the home page on click
    const handleClick = () => {
      if (isMangaPage) {
        window.location.href = '/'; // Navigate to home page
      }
    };

    return (
      <Link className="nav-link" to="/manga" style={{ marginRight: '10px' }} onClick={handleClick}>
        Manga
      </Link>
    );
  };

export default MangaLink;
