import React from "react";
import { Link, useLocation, useNavigate } from 'react-router-dom';

const MangaLink = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Check if the current location is the manga page
  const isMangaPage = location.pathname === '/manga';

  // If already on the manga page, navigate to dashboard on click
  const handleClick = () => {
    if (isMangaPage) {
      navigate('/'); // Navigate to dashboard
    }
  };

  return (
    <Link className="nav-link" to="/manga" style={{ marginRight: '10px' }} onClick={handleClick}>
      Manga
    </Link>
  );
};
export default MangaLink;
