import React, { useState } from "react";
import "./AlertCard.css";

export default function AlertCard({ breachedAccounts }) {
  const [showChangePassword, setShowChangePassword] = useState(false);

  const handleDismissAlert = () => {
    // Dismiss the alert and return it to the normal state
    setShowChangePassword(false);
  };

  return (
    <div className={`card alert-card ${breachedAccounts && !showChangePassword ? 'alert-card-red' : ''}`}>
      <div className="card-header alert-card-header">Alert</div>
      <div className="alert-card-content">
        {breachedAccounts && !showChangePassword ? (
          <React.Fragment>
            <p>Your email was involved in a breach on the following sites:</p>
            <ul>
              {breachedAccounts.map(breach => (
                <li key={breach.id}>{breach.domain}</li>
              ))}
            </ul>
            <div>
              <button onClick={() => setShowChangePassword(true)}>Change Password</button>
            </div>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <p>Prepare important customer report</p>
            <div>
              <button onClick={handleDismissAlert}>Dismiss</button>
            </div>
          </React.Fragment>
        )}
        {showChangePassword && (
          <div>
            <label>New Password:</label>
            <input type="password" />
            <div>
              <button>Submit</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
