import React from "react";
import { Link, useLocation, Navigate } from "react-router-dom";

const MangaLink = () => {
  const location = useLocation();

  // Define the link text based on the current location
  const linkText = location.pathname === "/manga" ? "Manga" : "Manga";

  return location.pathname === "/manga" ? (
    <Navigate to="/" replace />
  ) : (
    <Link className="nav-link" to="/manga" style={{ marginRight: "10px" }}>
      {linkText}
    </Link>
  );
};

export default MangaLink;
