import React, { useState, useEffect } from 'react';

const clientID = '6198fcf6f4eb4eda9e9bca8527177fd4';
const clientSecret = '5f3e5221e42d4ca695c2c1be5910747f';
let accessToken = ''; // Store the access token globally

async function getAccessToken() {
  const response = await fetch('https://accounts.spotify.com/api/token', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Authorization': `Basic ${btoa(`${clientID}:${clientSecret}`)}`
    },
    body: 'grant_type=client_credentials'
  });
  const data = await response.json();
  accessToken = data.access_token; // Update the global access token
  setTimeout(getAccessToken, 3600000); // Refresh token after one hour (3600000 milliseconds)
}

async function fetchWebApi(endpoint, method, body) {
  if (!accessToken) await getAccessToken();

  const res = await fetch(`https://api.spotify.com/${endpoint}`, {
    headers: {
      Authorization: `Bearer ${accessToken}`,
    },
    method,
    body: JSON.stringify(body)
  });
  return await res.json();
}

async function getTopTracks() {
  try {
    // Endpoint reference: https://developer.spotify.com/documentation/web-api/reference/get-users-top-artists-and-tracks
    const response = await fetchWebApi(
      'v1/me/top/tracks?time_range=long_term&limit=5', 'GET'
    );
    return response.items;
  } catch (error) {
    console.error('Error fetching top tracks:', error);
    return [];
  }
}

export default async function Spotify() {

    const [tracks, setTracks] = useState([]);

  useEffect(() => {
    async function fetchAndDisplayTopTracks() {
        const topTracks = await getTopTracks();
        setTracks(topTracks);
    }

    fetchAndDisplayTopTracks();
  },[]);

  return (
    <div>
        <h2>Top 5 Tracks</h2>
        <div className="songs-container">
            {tracks.map((track, index) => (
            <li key={index}>{track.name} by {track.artists.map(artist => artist.name).join(', ')}</li>
            ))}
        </div>
    </div>
);
}
