import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import LoginForm from "./LoginForm";
import Dashboard from "./Dashboard";
import Profile from "./Profile";

export default function App() {
  const [auth, setAuth] = useState({
    token: "",
    user: {},
    meta: {}
  });

  const onLoginSuccess = auth => {
    setAuth(auth);
  };

  const onLogOut = e => {
    setAuth({
      token: "",
      user: {},
      meta: {}
    });
  };

  const currentPage = () => {
    return auth.token ? (
      <Dashboard auth={auth} />
    ) : (
      <LoginForm onLoginSuccess={onLoginSuccess} />
    );
  };

  return (
    <Router>
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-info">
        <a className="navbar-brand mr-auto" href="#">AppCo</a>
        {auth.token && (
          <ul className="navbar-nav">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            {/* Link to the Tasks page */}
            <li className="nav-item">
                <Link className="nav-link" to="/tasks">Tasks</Link>
              </li>
            <li className="nav-item">
              <a className="nav-link" onClick={onLogOut}>Log out</a>
            </li>
          </ul>
        )}
      </nav>
      <div className="container">
          <Switch> {/* Use Switch to render only one Route at a time */}
            <Route exact path="/tasks" component={Profile} /> {/* Route to the Profile component when /tasks is accessed */}
            <Route path="/" render={() => currentPage()} /> {/* Route to Dashboard or LoginForm based on authentication */}
          </Switch>
      </div>
    </div>
    </Router>
  );
}
