export default function Spotify() {
    const [topTracks, setTopTracks] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
      async function fetchAndSetTopTracks() {
        try {
          const tracks = await getTopTracks();
          setTopTracks(tracks);
          setLoading(false);
        } catch (error) {
          console.error('Error fetching top tracks:', error);
          setLoading(false);
        }
      }

      fetchAndSetTopTracks();
    }, []);

    return (
      <div>
        <h2>Top 5 Tracks</h2>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <div>
            {topTracks.map((track, index) => (
              <div key={index}>
                <img style={{ marginRight: '20px', width: '90px', height: '50px'}} src={track.album.images[2].url}  alt={track.name} />
                <p>{track.name}  by {track.artists.map(artist => artist.name).join(', ')}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    );
}
