import React, { useState, useEffect } from 'react';
import axios from 'axios';
import cheerio from 'cheerio';

const Movies = () => {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
      const fetchMovies = async () => {
        try {
          const response = await axios.get('https://letterboxd.com/stoopidass/list/fav/');
          const html = response.data;
          const $ = cheerio.load(html);
          const movieElements = $('li.poster-list-item');
          const movieNames = movieElements.slice(0, 10).map((index, element) => {
            return $(element).find('img').attr('alt');
          }).get();
          setMovies(movieNames);
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };

      fetchMovies();
    }, []);

    return (
      <div>
        <h1>Top 10 Movies from Letterboxd List</h1>
        <ul>
          {movies.map((movie, index) => (
            <li key={index}>{movie}</li>
          ))}
        </ul>
      </div>
    );
  };

  export default Movies;
