import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Manga = () => {
    const [manga, setManga] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8000/scrap/manga');
        setManga(response.data);

        console.log(response.data);

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };



    fetchData();
  }, []);

  return (
    <div>
        <h2>Currently Reading</h2>
        <div className="manga-container">
            {manga.map((manga, index) => (
                <div key={index} className="manga">
                    <a href={`https://myanimelist.com${manga.title}`} target="_blank" rel="noopener noreferrer">
                            <img src={manga.imageUrl} alt={manga.title} />
                        </a>
                </div>
            ))}
        </div>
    </div>
);
}

  export default Manga;
