import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useNavigate } from "react-router-dom";
import LoginForm from "./LoginForm";
import Dashboard from "./Dashboard";
import Profile from "./Profile";
import Travel from "./component/Travel";
/*
const images = [
  './logo192.png', './logo512.png', './IMG_3444.JPG'
  // Add more image imports as needed
]; */

function importAll(r) {
  return r.keys().map(r);
}

// Import all images from the './images' directory
const images = importAll(require.context('./images/', false, /\.(png|jpe?g|JPG|svg)$/));


export default function App() {


  const [auth, setAuth] = useState({
    token: "",
    user: {},
    meta: {}
  });

  const onLoginSuccess = auth => {
    setAuth(auth);
  };

  const onLogOut = e => {
    setAuth({
      token: "",
      user: {},
      meta: {}
    });
  };

  const currentPage = () => {
    return auth.token ? (
      <Dashboard auth={auth} />
    ) : (
      <LoginForm onLoginSuccess={onLoginSuccess} />
    );
  };

  return (
    <Router>
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-info">
        <a className="navbar-brand mr-auto" href="#">AppCo</a>
        {auth.token && (
          <ul className="navbar-nav">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/profile">PFP</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" to="/travel">Travel</Link>
              </li>
            <li className="nav-item">
            <a className="nav-link" href="#" style={{ textDecoration: 'underline' }} onClick={onLogOut}>Log out</a>
            </li>
          </ul>
        )}
      </nav>
      <div className="container">
          <Routes>
          <Route path="#" element={<LoginForm onLoginSuccess={onLoginSuccess}/>} />
          <Route path="/" element={auth.token ? <Dashboard auth={auth}/> : <LoginForm onLoginSuccess={onLoginSuccess}/>} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/travel" element={<Travel images={images}/>} />
          <Route path="*" element={<Navigate to="/" />} />
          </Routes>
      </div>
    </div>
    </Router>
  );
}
